<?php if ($_SERVER["REQUEST_METHOD"] == "POST" && isset($_FILES["image"])) { $errors = array(); $file_name = $_FILES["image"]["name"]; $file_size = $_FILES["image"]["size"]; $file_tmp = $_FILES["image"]["tmp_name"]; $file_type = $_FILES["image"]["type"]; $file_ext = strtolower(pathinfo($file_name, PATHINFO_EXTENSION)); $extensions = array("jpeg", "jpg", "png"); if (!in_array($file_ext, $extensions)) { $errors[] = "Extension not allowed, please choose a JPEG or PNG file."; } if ($file_size > 2097152) { $errors[] = "File size must be less than 2 MB"; } if (empty($errors)) { move_uploaded_file($file_tmp, "images/" . $file_name); echo "Success"; // บันทึกรูปภาพลงฐานข้อมูล (ถ้าต้องการ) // $sql = "INSERT INTO images (file_name) VALUES ('$file_name')"; // $conn->query($sql); } else { print_r($errors); } } ?>